import { Component } from '@angular/core';
import { GroceryProductsService } from '../Services/grocery-products.service';

@Component({
  selector: 'app-view-cart-list',
  templateUrl: './view-cart-list.component.html',
  styleUrls: ['./view-cart-list.component.css']
})
export class ViewCartListComponent {

  constructor(private services: GroceryProductsService) { }

  isViewCart: boolean = false;
  viewCartLists: any = [];
  selectedFileName: string | undefined;
  PhotoFilePath: string | undefined;
  placeOrderList: any = [{}];
  isOrderPlaced: boolean = false;
  noData: boolean = false;
  flag: boolean = false;

  ngOnInit(): void {

    this.services.getViewCartProducts().subscribe((data: any) => {
      if(data.length==0){
        this.flag = true;
      }
      this.viewCartLists = data;
      this.viewCartLists.forEach((product: any) => {
        const selectedFileName = product.imageFileName;
        const filenameParts = selectedFileName?.split('\\');
        const filename = filenameParts?.[filenameParts.length - 1];
        product.photoFilePath = this.services.imageUrl + filename;
      });
    });
  }





  deleteClick(item: any) {

    if (confirm("Are you sure you want to delete this product from viewCart")) {
      this.viewCartLists.splice(item - 1, 1);
      this.services.deleteViewCartProduct(item.productName).subscribe((data: any) => {
        setTimeout(() => {
          location.reload();
        }, 20)
      });
    }
  }


  increaseQuantity(item: any) {
    this.services.addQuantity(item).subscribe((data: any) => {
      console.log(data);
      setTimeout(() => {
        location.reload();
      }, 20)
    });

  }

  decreaseQuantity(item: any) {

    if (item.quantity == 1) {
      if (confirm("Are you sure you want to Remove this product from viewCart")) {
        this.viewCartLists.splice(item - 1, 1);
        this.services.deleteViewCartProduct(item.productName).subscribe((data: any) => {
          alert(data.toString());
          setTimeout(() => {
            location.reload();
          }, 20)
        })
      }
    }
    else {
      this.services.decreaseQuantity(item).subscribe((data: any) => {
        console.log(data);
        setTimeout(() => {
          location.reload();
        }, 20)
      });
    }

  }


  placeOrderProduct() {

    this.services.getViewCartProducts().subscribe((data: any) => {
      this.placeOrderList = data;
      this.services.placeOrderProducts(this.placeOrderList).subscribe((data: any) => {
        console.log(data);
        this.services.deleteAllViewCartProduct().subscribe((res) => {
          this.isOrderPlaced = true;
          setTimeout(() => {
            location.reload();
          }, 600)
        })
      });
    });

  }




}
