import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http"
import { Observable, of } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class GroceryProductsService {
  imageUrl = "https://localhost:44395/GetImage/";
  url = "https://localhost:44395";
  productsUrl = "https://localhost:44395/product";

  constructor(private http:HttpClient) { }

 httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json'
    })
  };


  // Product API Methods

  getAllProducts(){
    return this.http.get(`https://localhost:44395/product/ProductDetails`);
  }

  AddProducts(data:any):Observable<any> {
    // debugger;
    return this.http.post(`${this.productsUrl}/AddProduct`, data);
  }

  getproductsById(id:any){
    return this.http.get(`${this.productsUrl}/productDetail?id=${id}`);
  }

  deleteProducts(id:any){
     return this.http.delete(`${this.productsUrl}/deleteProduct?id=${id}`);
  }

  UploadPhoto(val:any){
    return this.http.post(`${this.url}/UploadFile`,val);
  }

  getImage(val:any){
   return this.http.get(`${this.imageUrl}+${val}`);
  }

  addProductQuantity(data:any){
    return this.http.put(`${this.productsUrl}/AddQuantity`, data);
  }


  decreaseProductQuantity(data:any){
    return this.http.put(`${this.productsUrl}/DecreaseQuantity`, data);
  }


  // ViewCart API Methods

  getViewCartProducts(){
    // debugger;
    return this.http.get(`${this.url}/ViewCart`);
  }

  AddViewCartProducts(data: any): Observable<any> {
    // debugger;
    return this.http.post(`${this.url}/AddToViewCart`, data);
  }

  getViewCartProductById(id:any){
    return this.http.get(`${this.url}/ViewCartProductDetail?id=${id}`);
  }

  deleteViewCartProduct(productName:any){
    return this.http.delete(`${this.url}/DeleteViewCartProduct?productName=${productName}`);
  }

  addQuantity(data:any){
    return this.http.put(`${this.url}/AddQuantity`,data);
  }

  decreaseQuantity(data:any){
    return this.http.put(`${this.url}/DecreaseQuantity`,data);
  }

// MyOrders API Methods

getMyOrdersProducts(){

  return this.http.get(`${this.url}/MyOrders`);
}

}
