  dataAdded() {
    this.addProductForm.patchValue({
      imageFileName: this.selectedFileName || '' // Set the selectedFileName value if available, otherwise set an empty string
    });

    this.service.AddProducts(this.addProductForm.value).subscribe(
      (res) => {
        console.log(res);
        this.isProductAdded = true;
        this.addProductForm.reset();
        this.selectedFile = undefined;
        this.selectedFileName = undefined;
      },
      (error) => {
        console.error(error);
      }
    );
  }

  UploadPhoto(event: any) {
    const fileInput = event.target as HTMLInputElement;
    const files = fileInput.files;

    if (files && files.length > 0) {
      this.selectedFile = files[0];
      this.selectedFileName = this.selectedFile.name;

      const formdata: FormData = new FormData();
      formdata.append('file', this.selectedFile, this.selectedFileName);

      // Remove the line below or set the value to a valid filename
      // this.ImageFileName.setValue('');

      this.service.UploadPhoto(formdata).subscribe(
        (response: any) => {
          console.log(response.fileName);
          this.selectedFileName = response.fileName;
          this.ImageFileName.setValue(this.selectedFileName); // Set the value of the form control
        },
        (error: any) => {
          console.error(error);
        }
      );
    }
  }



