import { Component } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { JwtHelperService } from '@auth0/angular-jwt';
import { AuthService } from './Component/Services/auth.service';

import { GroceryProductsListComponent } from './Component/grocery-products-list/grocery-products-list.component';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  isLoggedIn: boolean = false;
  jwtHelpherSerivice = new JwtHelperService();

  menuType :  string = 'Default';
  userName:string="";

  constructor(private service: AuthService){}

ngOnInit(){
     this.setCurrentUser();
     this.service.currentUser.subscribe({
      next:(res)=>{

        this.setCurrentUser();
      }})
};

signOut(){

  this.service.isAdmin= false;
  this.service.signOut();

}  

  title = 'GroceryShopFrontend';



setCurrentUser(){
  var user =  localStorage.getItem("localUserData");
  if(user != null){
    var userInfo = JSON.parse(user);

    console.log(userInfo);
    this.menuType = userInfo.role;
    this.userName = userInfo.firstname;
    console.log(this.menuType);
    if(this.menuType =="Admin"){
       this.service.isAdmin = true;
    }
  }else{
          this.menuType = "Default";
  }



}

}
