import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GroceryProductsService } from '../Services/grocery-products.service';
import { AuthService } from '../Services/auth.service';

interface CartProduct {
  description: string;
  discount: number;
  imageFileName: string;
  price: number;
  productName: string;
  quantity: number;
  specification: string;
}

@Component({
  selector: 'app-product-detail-page',
  templateUrl: './product-detail-page.component.html',
  styleUrls: ['./product-detail-page.component.css']
})
export class ProductDetailPageComponent {
  isProductAdded: boolean = false;
  isUser: boolean = false;
  productDetail: any = {};
  constructor(
    private services: GroceryProductsService,
     private router: ActivatedRoute,
     private authService:AuthService,
     private route:Router) { }

  ngOnInit() {
    this.refreshProductDetail();
    this.isUser = this.authService.isUser();
  }


  refreshProductDetail() {
    this.router.queryParams.subscribe(params => {
      console.log(params);
      this.productDetail = params;
    });
  }

  increaseQuantity(item: any) {
    this.services.addProductQuantity(item).subscribe(
      (data: any) => {
        console.log(data);
        this.refreshProductDetail();
      },
      (error: any) => {
        console.error('Error adding product quantity:', error);
      }
    );
  }


  decreaseQuantity(item: any) {
    if (item.quantity == 1) {
      {
        this.refreshProductDetail();
      }
    }
    else {
      this.services.decreaseProductQuantity(item.id).subscribe((data: any) => {
        console.log(data);
        this.refreshProductDetail();
      });
    }
  }

  AddViewCart(id: any) {

    if(this.isUser){
      this.services.getproductsById(id).subscribe((result: any) => {
        const cartProduct: CartProduct = {
          description: result.description,
          discount: result.discount,
          imageFileName: result.imageFileName,
          price: result.price,
          productName: result.productName,
          quantity: result.quantity,
          specification: result.specification
        };

        this.services.AddViewCartProducts(cartProduct).subscribe((res: any) => {
          console.warn(res);
          this.isProductAdded = true;
        });
      });
    }

    else{
      this.route.navigate(['Login']);
    }



  }


  closeAlert() {
    this.isProductAdded = false;
  }

}
