import { Component } from '@angular/core';
import { GroceryProductsService } from '../Services/grocery-products.service';

@Component({
  selector: 'app-view-cart-list',
  templateUrl: './view-cart-list.component.html',
  styleUrls: ['./view-cart-list.component.css']
})
export class ViewCartListComponent {

  constructor(private services: GroceryProductsService){}

  isViewCart: boolean = false;
  viewCartLists:any = [];


  ngOnInit():void{
   this.refreshViewCartList();
  }

  refreshViewCartList(){

    this.services.getViewCartProducts().subscribe((data:any)=>{
      if(data!=null)
         {
          this.isViewCart= false;
          this.viewCartLists = data;
          }  
      else{
        this.isViewCart=true;
      } 
    });
  }




  deleteClick(item:any){

    if(confirm("Are you sure you want to delete this product from viewCart")){
      this.viewCartLists.splice(item-1,1);
      this.services.deleteViewCartProduct(item.productName).subscribe((data:any)=>{
        alert(data.toString());
        this.refreshViewCartList();
      })
    }
  }

  increaseQuantity(item:any){
      this.services.addQuantity(item).subscribe((data:any)=>{
        console.log(data);
         this.refreshViewCartList();
       });

  }

  decreaseQuantity(item:any){

    if(item.quantity==1){
      if(confirm("Are you sure you want to Remove this product from viewCart")){
        this.viewCartLists.splice(item-1,1);
        this.services.deleteViewCartProduct(item.productName).subscribe((data:any)=>{
          alert(data.toString());
          this.refreshViewCartList();
        })
      }
    }
    else{
      this.services.decreaseQuantity(item).subscribe((data:any)=>{
        console.log(data);
         this.refreshViewCartList();
       });
    }

  }


}

    <div class="container" *ngIf="!isViewCart">

      <h1>View Cart Products</h1>
      <div class="row" *ngFor="let item of viewCartLists">



           <div class="col detailCard">

            <div class="img">
              <img src="../../../assets/Juice.jpg" alt="img">
            </div>
            <div class="detail">
              <h3>{{item.productName | titlecase}}</h3>
              <br>
              <p class="discount">Upto {{item.discount}}% off</p>
              <br>
              <p class="price"> <i class="fa fa-inr"></i>{{item.price}}</p>
            </div>


            <div class="operation">
              <div class="qauntity">
               <div (click)="decreaseQuantity(item)" class="opSymbol" data-toggle="tooltip" data-placement="bottom" title="Decrease Quantity"><i class="fa fa-minus"></i></div>
               <div class="value" style="margin-right: 12px; margin-left:12px;"><p>{{item.quantity}}</p></div>
               <div (click)="increaseQuantity(item)" class="opSymbol" data-toggle="tooltip" data-placement="bottom" title="Increase Quantity"><i   class="fa fa-plus" ></i></div>
              </div> 
              <div class="col deleteCart"><i  (click)="deleteClick(item)" class="fa fa-shopping-cart" data-toggle="tooltip" data-placement="bottom" title="Delete from Cart" data-toggle="modal" data-target="#exampleModal" ></i> </div>
              </div>

           </div>      
      </div>
      <div class="btn mt-4">
        <button class="orderPlace">Place Order</button>
      </div>
    </div>



    <div class="container" *ngIf="isViewCart">
      <div class="noOrders"  >
         <h1>You didn't  Add Any Product to View Cart.</h1>
         <br>
         <a class="productPageButton" routerLink ="/">Go To Product Page</a>
      </div>
    </div>
