import { Component } from '@angular/core';
import { GroceryProductsService } from '../Services/grocery-products.service';

@Component({
  selector: 'app-my-order-list',
  templateUrl: './my-order-list.component.html',
  styleUrls: ['./my-order-list.component.css']
})
export class MyOrderListComponent {

  constructor(private services: GroceryProductsService){}

  myOrdersLists:any = [];
  isMyordered:boolean = true;

  ngOnInit():void{
   this.getAllMyOrdersProducts();
  }



  getAllMyOrdersProducts(){
    this.services.getMyOrdersProducts().subscribe((data:any)=>{
      if(data!=null){
        this.myOrdersLists = data;
    }else{
        this.isMyordered=true;
    }
  });
  }

}
