import { Component } from '@angular/core';
import { GroceryProductsService } from '../grocery-products.service';

@Component({
  selector: 'app-grocery-products-list',
  templateUrl: './grocery-products-list.component.html',
  styleUrls: ['./grocery-products-list.component.css']
})
export class GroceryProductsListComponent {

  constructor(private services: GroceryProductsService){}

  productsList :any = [];

  ngOnInit():void{
    this.services.getAllProducts().subscribe((result:any)=>{
      this.productsList = result;
    })
  }



}
