import { Component } from '@angular/core';
import {FormGroup,FormControl, Validators, AbstractControl, ValidationErrors} from '@angular/forms';
import { GroceryProductsService } from '../Services/grocery-products.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {

constructor(private service: GroceryProductsService){}

addProduct = new FormGroup({
  productName: new FormControl("",
  [Validators.required,
    Validators.minLength(4),
    Validators.pattern("[a-zA-Z].*")
  ]),
  description: new FormControl("",
  [Validators.required,
    Validators.minLength(2),
    Validators.pattern("[a-zA-Z].*")
  ]),
  qauntity: new FormControl("",
  [Validators.required,
    Validators.pattern("[1-50]*")
  ]),

 image: new FormControl("",
  [Validators.required]),

  price: new FormControl("",
  [Validators.required,
    Validators.pattern("[1-50000000000000000]*")
  ]),
  discount: new FormControl("",[Validators.required,
    Validators.pattern("[1-95]*")]),

    Specification: new FormControl("",[Validators.required,
      Validators.pattern("[1-9].*")])

})

}
