import { Component, ViewChild, ElementRef } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { GroceryProductsService } from '../Services/grocery-products.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  @ViewChild('fileInput') fileInput!: ElementRef;

  selectedFile: File | undefined;
  selectedFileName: string | undefined;
  PhotoFileName: any;

  isProductAdded: boolean = false;

  constructor(private service: GroceryProductsService) {}

  addProductForm = new FormGroup({
    productName: new FormControl("", [
      Validators.required,
      Validators.minLength(4),
      Validators.pattern("[a-zA-Z].*")
    ]),
    description: new FormControl("", [
      Validators.required,
      Validators.minLength(5)
    ]),
    quantity: new FormControl("", [
      Validators.required,
      Validators.pattern("[1-50]*")
    ]),
    imageFileName: new FormControl("", [Validators.required]),
    price: new FormControl("", [Validators.required]),
    discount: new FormControl("", [
      Validators.required,
      Validators.pattern("[1-95]*")
    ]),
    specification: new FormControl("", [Validators.required])
  });

  get ProductName(): FormControl {
    return this.addProductForm.get("productName") as FormControl;
  }

  get Description(): FormControl {
    return this.addProductForm.get("description") as FormControl;
  }

  get Quantity(): FormControl {
    return this.addProductForm.get("quantity") as FormControl;
  }

  get ImageFileName(): FormControl {
    return this.addProductForm.get("imageFileName") as FormControl;
  }

  get Price(): FormControl {
    return this.addProductForm.get("price") as FormControl;
  }

  get Discount(): FormControl {
    return this.addProductForm.get("discount") as FormControl;
  }

  get Specification(): FormControl {
    return this.addProductForm.get("specification") as FormControl;
  }

  dataAdded() {
    this.service.AddProducts(this.addProductForm.value).subscribe(
      (res) => {
        console.log(res);
        this.isProductAdded = true;
        this.addProductForm.reset();
        this.selectedFile = undefined;
        this.selectedFileName = undefined;
      },
      (error) => {
        console.error(error);
      }
    );
  }

  // UploadPhoto(event: any) {
  //   this.selectedFile = event.target.files[0];
  //   this.selectedFileName = this.selectedFile ? this.selectedFile.name : undefined;

  //   const formdata: FormData = new FormData();
  //   if (this.selectedFile) {
  //     formdata.append('file', this.selectedFile, this.selectedFileName);
  //   }

  //   this.service.UploadPhoto(formdata).subscribe(
  //     (response: any) => {
  //       console.log(response.fileName);
  //       this.selectedFileName = response.fileName;
  //     },
  //     (error: any) => {
  //       console.error(error);
  //     }
  //   );
  // }

  UploadPhoto(event: any) {
    this.selectedFile = event.target.files[0];
    this.selectedFileName = this.selectedFile ? this.selectedFile.name : undefined;

    const formdata: FormData = new FormData();
    if (this.selectedFile) {
      formdata.append('file', this.selectedFile, this.selectedFileName);
    }

    this.service.UploadPhoto(formdata).subscribe(
      (response: any) => {
        console.log(response.fileName);
        this.selectedFileName = response.fileName;

        // Extract the filename without the path
        const filenameParts = this.selectedFileName?.split('\\');
        this.selectedFileName = filenameParts?.[filenameParts.length - 1];
      },
      (error: any) => {
        console.error(error);
      }
    );
  }








}  
<div class="container">
    <h2>Add New Product</h2>
    <div *ngIf="isProductAdded" class="alert alert-success alert-dismissible fade show" role="alert">
      <strong>Success!</strong> Product is Added SuccessFully .
      <button type="button" class="close" data-dismiss="alert" aria-label="Close">
        <span aria-hidden="true">&times;</span>
      </button>
    </div>

    <form [formGroup]="addProductForm" (ngSubmit)="dataAdded()" >
      <div class="row mb-3">
        <label for="productName" class="form-label">Product Name</label>
        <input type="text" class="form-control" id="productName" placeholder="Enter product name" formControlName="productName">

        <div class="form-text text-danger " *ngIf="ProductName.errors?.['required'] && (ProductName.touched || ProductName.dirty)">
          Product name is required
        </div>
        <div class="form-text text-danger" *ngIf="ProductName.errors?.['minLength']">
          Product name is to Short
        </div>
        <div class="form-text text-danger" *ngIf="ProductName.errors?.['pattern'] && (ProductName.touched || ProductName.dirty)">
          Product name should Start with Aplhabet
        </div>

      </div>
      <div class="row mb-3">
        <label for="description" class="form-label">Description</label>
        <textarea class="form-control" id="description" rows="3" placeholder="Enter description" formControlName="description"></textarea>

        <div class="form-text text-danger " *ngIf="Description.errors?.['required'] && (Description.touched || Description.dirty)">
          Description is required
        </div>
        <div class="form-text text-danger" *ngIf="Description.errors?.['minLength']">
          Description is too short
        </div>


      </div>
      <div class="row mb-3">
        <div class="col">
            <label for="quantity" class="form-label">Quantity</label>
        <input type="number" class="form-control" id="quantity" placeholder="Enter quantity" formControlName="quantity">

        <div class="form-text text-danger " *ngIf="Quantity.errors?.['required'] && (Quantity.touched || Quantity.dirty)">
          Qauntity is required
        </div>
        <div class="form-text text-danger" *ngIf="Quantity.errors?.['pattern'] && (Quantity.touched || Quantity.dirty)">
          Qauntity should not more than 50
        </div>


        </div>

        <div class="col"><label for="imageFileName" class="form-label">Image</label>
            <input type="file" name="imageFileName" class="form-control" id="imageFileName" 
            formControlName="imageFileName" 

            (change)="UploadPhoto($event)"   

             >

             <!-- #fileInput
            accept="image/*"  -->

            <p>Selected file: {{ selectedFileName }}</p>

            <div class="form-text text-danger " *ngIf="ImageFileName.errors?.['required'] && (ImageFileName.touched || ImageFileName.dirty)">
              Image is required
            </div>

        </div>
      </div>
      <div class="row mb-3">

      </div>
      <div class="row mb-3">
        <div class="col">
            <label for="price" class="form-label">Price</label>
            <input type="number" class="form-control" id="price" placeholder="Enter price" formControlName="price">    

            <div class="form-text text-danger " *ngIf="Price.errors?.['required'] && (Price.touched || Price.dirty)">
              Price is required
            </div>


        </div>

       <div class="col">
        <label for="discount" class="form-label">Discount</label>
        <input type="number" class="form-control" id="discount" placeholder="Enter discount" formControlName="discount">

        <div class="form-text text-danger " *ngIf="Discount.errors?.['required'] && (Discount.touched || Discount.dirty)">
          Discount is required
        </div>
        <div class="form-text text-danger" *ngIf="Discount.errors?.['pattern'] && (Discount.touched || Discount.dirty)">
          Discount not more than 95%
        </div>



       </div>

      </div>
      <div class="row mb-3">
      </div>
      <div class="row mb-3">
        <label for="specification" class="form-label">Specification</label>
        <textarea class="form-control" id="specification" rows="3" placeholder="Enter specification" formControlName="specification"></textarea>

        <div class="form-text text-danger " *ngIf="Specification.errors?.['required'] && (Specification.touched || Specification.dirty)">
          Specification is required
        </div>

      </div>
      <button type="submit" class="btn btn-primary" >Add Product</button>
    </form>
  </div>
