import { Component } from '@angular/core';
import { GroceryProductsService } from '../Services/grocery-products.service';


interface CartProduct {
  description: string;
  discount: number;
  imageFileName: string;
  price: number;
  productName: string;
  quantity: number;
  specification: string;
}

@Component({
  selector: 'app-grocery-products-list',
  templateUrl: './grocery-products-list.component.html',
  styleUrls: ['./grocery-products-list.component.css']
})
export class GroceryProductsListComponent {

  constructor(private services: GroceryProductsService){}

  productsList :any = [];



  ngOnInit():void{
    this.services.getAllProducts().subscribe((result:any)=>{
      this.productsList = result;
      console.warn(this.productsList);
    })
  }

  AddViewCart(id: any) {
    this.services.getproductsById(id).subscribe((result: any) => {
      const cartProduct: CartProduct = {
        description: result.description,
        discount: result.discount,
        imageFileName: result.imageFileName,
        price: result.price,
        productName: result.productName,
        quantity: result.quantity,
        specification: result.specification
      };

      this.services.AddViewCartProducts(cartProduct).subscribe((res: any) => {
        console.warn(res);
      });
    });
  }




}
