import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { GroceryProductsService } from '../Services/grocery-products.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent {
  PhotoFilePath: any;
  isProductAdded: boolean = false;

  constructor(private service: GroceryProductsService) {}

  addProductForm = new FormGroup({
    productName: new FormControl("", [
      Validators.required,
      Validators.minLength(4),
      Validators.pattern("[a-zA-Z].*")
    ]),
    description: new FormControl("", [
      Validators.required,
      Validators.minLength(5),
      Validators.pattern("[a-zA-Z]*")
    ]),
    quantity: new FormControl("", [
      Validators.required,
      Validators.pattern("[1-9]|[1-4][0-9]|50")
    ]),
    image: new FormControl("", [Validators.required]),
    price: new FormControl("", [Validators.required]),
    discount: new FormControl("", [
      Validators.required,
      Validators.pattern("[1-9][0-5]?")
    ]),
    specification: new FormControl("", [Validators.required])
  });

  get ProductName(): FormControl {
    return this.addProductForm.get("productName") as FormControl;
  }

  get Description(): FormControl {
    return this.addProductForm.get("description") as FormControl;
  }

  get Quantity(): FormControl {
    return this.addProductForm.get("quantity") as FormControl;
  }

  get Image(): FormControl {
    return this.addProductForm.get("image") as FormControl;
  }

  get Price(): FormControl {
    return this.addProductForm.get("price") as FormControl;
  }

  get Discount(): FormControl {
    return this.addProductForm.get("discount") as FormControl;
  }

  get Specification(): FormControl {
    return this.addProductForm.get("specification") as FormControl;
  }

  dataAdded() {
    const formData: FormData = new FormData();
formData.append('productName', this.addProductForm.get('productName')!.value || '');
formData.append('description', this.addProductForm.get('description')!.value || '');
formData.append('quantity', this.addProductForm.get('quantity')!.value || '');

const imageFile = (document.getElementById('image') as HTMLInputElement).files?.[0];
formData.append('image', imageFile || '');

formData.append('price', this.addProductForm.get('price')!.value || '');
formData.append('discount', this.addProductForm.get('discount')!.value || '');
formData.append('specification', this.addProductForm.get('specification')!.value || '');

// Reset the file input element
(this.addProductForm.get('image') as FormControl).setValue(null);

    this.service.AddProducts(formData).subscribe(
      (res) => {
        console.log(res);
        this.isProductAdded = true;
        this.addProductForm.reset({});
      },
      (error) => {
        console.error(error);
      }
    );
  }

  UploadPhoto(event: any) {
    const file = event.target.files[0];
    this.addProductForm.patchValue({ image: file });
    this.addProductForm.get('image')?.updateValueAndValidity();
    const reader = new FileReader();
    reader.onload = () => {
      this.PhotoFilePath = reader.result;
    };
    reader.readAsDataURL(file);
  }

  closeAlert() {
    this.isProductAdded = false;
  }
}
