import { Component } from '@angular/core';
import { GroceryProductsService } from '../Services/grocery-products.service';
import { ActivatedRoute, Router } from '@angular/router';
import { AuthService } from '../Services/auth.service';


interface CartProduct {
  description: string;
  discount: number;
  imageFileName: string;
  price: number;
  productName: string;
  quantity: number;
  specification: string;
}

@Component({
  selector: 'app-grocery-products-list',
  templateUrl: './grocery-products-list.component.html',
  styleUrls: ['./grocery-products-list.component.css']
})
export class GroceryProductsListComponent {

  selectedFileName: string | undefined;
  PhotoFilePath: string | undefined;
  isProductDataEmpty: boolean = false;
  isproductListAvailable: boolean = false;
  isDeleted: boolean = false;
  isAdmin: boolean = false;

  imageUrl: any;
  title = "pagination";
  productsList: any = [];
  page: number = 1;
  count: number = 0;
  tableSize: number = 4;
  tableSizes: any = [5, 5, 5, 5];

  ProductNameFilter: string = "";
  ProductCategoryFilter: string ="";
  ProductListWithoutFilter: any = [];

  searchProduct() {
    var ProductNameFilter = this.ProductNameFilter;
    this.productsList = this.ProductListWithoutFilter.filter(function (el: any) {
      return el.productName.toString().toLowerCase().includes(ProductNameFilter.toLowerCase().toString().trim())
    });
  }

  sortResult(prop:any,asc:any){
    this.productsList = this.ProductListWithoutFilter.sort(function(a:any,b:any){
      if(asc){
        return (a[prop]>b[prop])?1: ((a[prop]<b[prop]) ?-1 : 0);

      }else{
          return (b[prop]>a[prop])?1: ((b[prop]<a[prop]) ?-1:0);
      }
    })
  }



  constructor(
    private services: GroceryProductsService,
    private route: Router,
    private AuthService: AuthService
  ) { }


  ngOnInit(): void {
    this.isAdmin = this.AuthService.isAdmin();
    this.services.getAllProducts().subscribe((res: any) => {

      this.productsList = res;
      if (this.productsList.length == 0) {
        this.isProductDataEmpty = true;
      }

        console.warn(this.productsList);
        this.ProductListWithoutFilter = this.productsList;

        this.productsList.forEach((product: any) => {
          const selectedFileName = product.imageFileName;
          const filenameParts = selectedFileName?.split('\\');
          const filename = filenameParts?.[filenameParts.length - 1];
          product.photoFilePath = this.services.imageUrl + filename;
        });

    });

    if(this.isAdmin){
      this.isProductDataEmpty = false;
    }else{

    }

  }

  onTableDataChanged(event: any) {
    this.page = event;
    setTimeout(() => {
      location.reload();
    }, 1000000000)
  }

  onTableSizeChanged(event: any) {
    this.tableSize = event.target.value;
    this.page = 1;
    setTimeout(() => {
      location.reload();
    }, 20)
  }


  AddViewCart(id: any) {
    this.services.getproductsById(id).subscribe((result: any) => {
      const cartProduct: CartProduct = {
        description: result.description,
        discount: result.discount,
        imageFileName: result.imageFileName,
        price: result.price,
        productName: result.productName,
        quantity: result.quantity,
        specification: result.specification
      };

      this.services.AddViewCartProducts(cartProduct).subscribe((res: any) => {
        console.warn(res);
      });
    });
  }

  deleteproducts(id: any) {
    if (confirm("Are you sure you want to delete this product from viewCart")) {
      this.services.deleteproducts(id).subscribe((data: any) => {
        setTimeout(() => {
          location.reload();
        }, 400)
        this.isDeleted = true;
      })
    }
  }

  collectData(productDetail: any) {
    this.route.navigate(['productDetail'], { queryParams: productDetail });
  }

  closeClick() {
    this.isDeleted = false;
  }
}

<div class="container" *ngIf="!isProductDataEmpty">

  <div *ngIf="isDeleted" class="alert alert-danger alert-dismissible fade show" role="alert">
    <strong>Deleted! </strong>Product is Deleted SuccessFully.
    <button type="button" (click)="closeClick()" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
    </button>
  </div>

  <div class="search-container">


    <div class="row"> 
      <div class="col sortBar mt-4">
        <button type="button" class="btn btn-light" 
        (click) =" sortResult('productName',true)">Up</button>
        <button type="button" class="btn btn-light" 
        (click) =" sortResult('productName',false)">Down</button>

      </div>
        <div class=" col searchbar mb-3">
          <i class="fa fa-search" aria-hidden="true"></i>
          <input [(ngModel)]="ProductNameFilter" type="search" class="search-input" placeholder="Search product"
            (keyup)="searchProduct()">
        </div>

    </div>

  <table class="table-striped table-hover">
    <thead class="table-heading">
      <tr>
        <th>Product</th>
        <th>Description</th>
        <th>Price</th>
        <th>Image</th>
        <th *ngIf="isAdmin">Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let item of productsList | paginate:{
          itemsPerPage:tableSize,
          currentPage:page,
          totalItems:count } let i= index ">

        <td (click)="collectData(item)">{{ item.productName }}</td>
        <td (click)="collectData(item)">{{ item.description }}</td>
        <td (click)="collectData(item)"><i class="fa fa-inr"></i> {{ item.price }}</td>
        <td (click)="collectData(item)"><img [src]="item.photoFilePath" class="product-image"></td>
        <td class=" mt-5 " *ngIf="isAdmin">
          <div class="row" style="border: none;">
            <div class="col">
              <button routerLink="/UpdateProduct/{{item.id}}" class="btn btn-primary">Update</button>
            </div>
            <div class="col" (click)="deleteproducts(item.id)">
              <button class="btn btn-danger" data-bs-toggle="modal" data-bs-target="#DeleteOperation">Delete</button>
            </div>
          </div>
        </td>
      </tr>
    </tbody>
  </table>

  <!-- Pagination Logic -->
  <div class=" pagination justify-content-center mt-3">
    <pagination-controls previousLabel="Prev" nextLabel="Next"
      (pageChange)="onTableDataChanged($event)"></pagination-controls>
  </div>
</div>

<div class="container mt-5" *ngIf="isProductDataEmpty">
  <div class="noData">
    <h1>Nothing Is in Product Database. Login As Admin and add Products to DataBase.</h1>
    <br>
    <a class="loginPageButton" routerLink="./Login">Login </a>
  </div>
</div>
