import { Component } from '@angular/core';
import { GroceryProductsService } from '../grocery-products.service';

@Component({
  selector: 'app-view-cart-list',
  templateUrl: './view-cart-list.component.html',
  styleUrls: ['./view-cart-list.component.css']
})
export class ViewCartListComponent {

  constructor(private services: GroceryProductsService){}

  viewCartLists:any = [];


  ngOnInit():void{
    this.services.getAllProducts().subscribe((result:any)=>{
      this.viewCartLists = result;
      console.warn(this.viewCartLists);
    })
  }

}
