import { Component } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { JwtHelperService } from '@auth0/angular-jwt';
import { AuthService } from './Component/Services/auth.service';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  currentUser : BehaviorSubject<any> = new BehaviorSubject(null);
  isLoggedIn: boolean = false;
  jwtHelpherSerivice = new JwtHelperService();

  menuType :  string = 'Default';
  userName:string="";

  constructor(private Service: AuthService){}

ngOnInit(){

};

  title = 'GroceryShopFrontend';

  setToken(token :string){
    localStorage.setItem("access_token", token);
    this.loadCurrentUser();
  }

loadCurrentUser(){
  const token = localStorage.getItem("access_token");
  const userInfo = token !=null ? this.jwtHelpherSerivice.decodeToken(token):null;
 const data = userInfo ? 
 {
      id:userInfo.id,
      firstname:userInfo.firstname,
      lastname:userInfo.lastname,
      email:userInfo.email,
      mobile:userInfo.mobile,
      role:userInfo.role,
 } : null;

 localStorage.setItem("localUserData",  JSON.stringify(userInfo.role));
 this.currentUser.next(data);
  console.log(userInfo);
  this.menuType = userInfo.role;
  console.log(this.menuType);
}

}
