import { Component, ViewChild, ElementRef } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { GroceryProductsService } from '../Services/grocery-products.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-update-product-list',
  templateUrl: './update-product-list.component.html',
  styleUrls: ['./update-product-list.component.css']
})

export class UpdateProductListComponent {
  @ViewChild('fileInput') fileInput!: ElementRef;

  selectedFile: File | undefined;
  selectedFileName: string | undefined;
  PhotoFileName: any;
  PhotoFilePath: any;
  changedPhotoFilePath: any;
  isProductAdded: boolean = false;
  isChanged: boolean = false;
  phottFIle: any;

  constructor(private service: GroceryProductsService, private router: ActivatedRoute) { }
  id: any = this.router.snapshot.params['id'];



  UpdatedProductForm = new FormGroup({
    productName: new FormControl(""),
    description: new FormControl(""),
    quantity: new FormControl(""),
    imageFileName: new FormControl(""),
    price: new FormControl(""),
    discount: new FormControl(""),
    specification: new FormControl(""),
    avaiableQuantity: new FormControl(""),
    category: new FormControl("")
  });

  ngOnInit() {
    console.warn(this.id);
    this.service.getproductsById(this.id).
      subscribe((result: any) => {

        const selectedFileName = result.imageFileName;
        const filenameParts = selectedFileName?.split('\\');
        const filename = filenameParts?.[filenameParts.length - 1];
        this.PhotoFileName = filename;

        this.phottFIle = this.service.imageUrl + filename;
        this.PhotoFilePath = this.phottFIle;
        this.isChanged = false;

        this.UpdatedProductForm = new FormGroup({

          productName: new FormControl(result["productName"]),
          description: new FormControl(result["description"]),
          quantity: new FormControl(result["quantity"]),
          imageFileName: new FormControl(),
          price: new FormControl(result["price"]),
          discount: new FormControl(result["discount"]),
          specification: new FormControl(result["specification"]),
          avaiableQuantity: new FormControl(result["avaiableQuantity"]),
          category: new FormControl(result["category"])
        });


      })

  }

  collectUpdatedData() {
    console.warn(this.id, this.UpdatedProductForm.value);
    this.isProductAdded = true;
    this.service.updateProduct(this.UpdatedProductForm.value, this.id).
      subscribe((result: any) => {
        console.warn(result);

      });
  }


  UploadPhoto(event: any) {
    this.selectedFile = event.target.files[0];
    this.selectedFileName = this.selectedFile ? this.selectedFile.name : undefined;
    const formdata: FormData = new FormData();
    if (this.selectedFile) {
      formdata.append('file', this.selectedFile, this.selectedFileName);
    }

    this.service.UploadPhoto(formdata).subscribe(
      (response: any) => {
        console.log(response.fileName);
        this.selectedFileName = response.fileName;
        const filenameParts = this.selectedFileName?.split('\\');
        this.selectedFileName = filenameParts?.[filenameParts.length - 1];
        this.changedPhotoFilePath = this.service.imageUrl + this.selectedFileName;
        this.isChanged = true;
      },
      (error: any) => {
        console.error(error);
      }
    );
  }

  closeClick() {
    this.isProductAdded = false;
  }
}
