import { Component, ViewChild, ElementRef } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { GroceryProductsService } from '../Services/grocery-products.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-update-product-list',
  templateUrl: './update-product-list.component.html',
  styleUrls: ['./update-product-list.component.css']
})

export class UpdateProductListComponent {
  @ViewChild('fileInput') fileInput!: ElementRef;

  selectedFile: File | undefined;
  selectedFileName: string | undefined;
  PhotoFileName: any;
  PhotoFilePath:any;
  changedPhotoFilePath:any;
  isProductAdded: boolean = false;
  isChanged: boolean = false;
  phottFIle: any;

  constructor(private service: GroceryProductsService,private router:ActivatedRoute) {}
  id:any =this.router.snapshot.params['id'];



  UpdatedProductForm = new FormGroup({
    productName: new FormControl(""),
    description: new FormControl(""),
    quantity: new FormControl(""),
    imageFileName: new FormControl(""),
    price: new FormControl(""),
    discount: new FormControl(""),
    specification: new FormControl("")
  });

  ngOnInit() {
    console.warn(this.id);
    this.service.getproductsById(this.id).
    subscribe((result:any)=>{

      const selectedFileName = result.imageFileName;
      const filenameParts = selectedFileName?.split('\\');
      const filename = filenameParts?.[filenameParts.length - 1];
      this.PhotoFileName = filename;

      this.phottFIle = this.service.imageUrl + filename;
      this.PhotoFilePath = this.phottFIle;
      this.isChanged=false;

      this.UpdatedProductForm = new FormGroup({

        productName: new FormControl(result["productName"]),
        description: new FormControl(result["description"]),
        quantity: new FormControl(result["quantity"]),
        imageFileName: new FormControl("imageFileName"),
        price: new FormControl(result["price"]),
        discount: new FormControl(result["discount"]),
        specification: new FormControl(result["specification"])
      });


    })

  }

  collectUpdatedData(){
    console.warn(this.id,this.UpdatedProductForm.value);
    this.service.updateProduct(this.UpdatedProductForm.value,this.id).
    subscribe((result:any)=>{
      console.warn(result);
      this.isProductAdded = true;
    });
  }


  UploadPhoto(event: any) {
    this.selectedFile = event.target.files[0];
    this.selectedFileName = this.selectedFile ? this.selectedFile.name : undefined;

    const formdata: FormData = new FormData();
    if (this.selectedFile) {
      formdata.append('file', this.selectedFile, this.selectedFileName);
    }

    this.service.UploadPhoto(formdata).subscribe(
      (response: any) => {
        console.log(response.fileName);
        this.selectedFileName = response.fileName;
        const filenameParts = this.selectedFileName?.split('\\');
        this.selectedFileName = filenameParts?.[filenameParts.length - 1];
        this.changedPhotoFilePath = this.service.imageUrl + this.selectedFileName;
        this.isChanged=true;
      },
      (error: any) => {
        console.error(error);
      }
    );
}
}
<div class="container">
    <h2>Update Product Details</h2>
    <div *ngIf="isProductAdded" class="alert alert-success alert-dismissible fade show" role="alert">
      <strong>Success!</strong> Product is Updated SuccessFully .
      <button type="button" class="close" data-dismiss="alert" aria-label="Close">
        <span aria-hidden="true">&times;</span>
      </button>
    </div>

    <form [formGroup]="UpdatedProductForm" (ngSubmit)="collectUpdatedData()" >
      <div class="row mb-3">
        <label for="productName" class="form-label">Product Name</label>
        <input type="text" class="form-control" id="productName" placeholder="Enter product name" formControlName="productName">
      </div>

      <div class="row mb-3">
        <label for="description" class="form-label">Description</label>
        <textarea class="form-control" id="description" rows="3" placeholder="Enter description" formControlName="description"></textarea>
      </div>

      <div class="row mb-3">
        <div class="col">
            <label for="quantity" class="form-label">Quantity</label>
        <input type="number" class="form-control" id="quantity" placeholder="Enter quantity" formControlName="quantity">
        </div>


      </div>
      <div class="row mb-3">
        <div class="col"><label for="imageFileName" class="form-label">Image</label>
            <input type="file" name="imageFileName" class="form-control" id="imageFileName" 
            formControlName="imageFileName" 
            (change)="UploadPhoto($event)"  >
            <p *ngIf="!isChanged">Selected file: {{ PhotoFileName }}</p>
            <p *ngIf="isChanged">Selected file: {{ selectedFileName }}</p>
        </div>
            <div *ngIf="!isChanged" class="col"><img style="width:100px; height:100px" [src]="PhotoFilePath" [alt]="PhotoFileName"></div>
            <div *ngIf="isChanged" class="col"><img style="width:100px; height:100px" [src]="changedPhotoFilePath" [alt]="changedPhotoFilePath"></div>
      </div>
      <div class="row mb-3">
        <div class="col">
            <label for="price" class="form-label">Price</label>
            <input type="number" class="form-control" id="price" placeholder="Enter price" formControlName="price">    
        </div>

       <div class="col">
           <label for="discount" class="form-label">Discount</label>
             <input type="number" class="form-control" id="discount" placeholder="Enter discount" formControlName="discount">
       </div>

      </div>
      <div class="row mb-3">
      </div>
      <div class="row mb-3">
        <label for="specification" class="form-label">Specification</label>
        <textarea class="form-control" id="specification" rows="3" placeholder="Enter specification" formControlName="specification"></textarea>
      </div>
      <button type="submit" class="btn btn-primary" >Update Product</button>
    </form>
  </div>
